sys_variable_value.value:
(function execute(inputs, outputs){
  try {
         var gl = new global.GSLog("sn_vul.usb.logging.verbosity", "DT Vuln Definitions Download");
         var NextCursor, fileName, HttpResp, Resp, load_status =  true;
         var OutDefnBuf, DefnCount, runCount = 0, totDefnCount, pLabel;
         var AttCount = 0;
         var pLabel;

         if (inputs.RespStat != 200) {
                  gl.logError(gs.getMessage('sn_vul', " Connection Issue. Check Logs. " + inputs.RespStat ));
                  load_status = false;     
         } else if (inputs.RespBody == 'no data')  {
                  gl.logError(gs.getMessage('sn_vul', " The Response Body is empty: "));
                  load_status = false;
         } else {
                 pLabel = " Before the FIRST HttpResp " + inputs.RespBody.length;
                 HttpResp = JSON.parse(inputs.RespBody);
		         totDefnCount = HttpResp["pagination_information"]["total_count"];
				 OutDefnBuf = {"security_findings": []};

                 pLabel = " After HttpResp " ;
                 var SecFnd = HttpResp["security_findings"];
				 for (var d = 0;d < SecFnd.length; d++) {
					  OutDefnBuf["security_findings"].push(SecFnd[d]);
                 } 
				 DefnCount = SecFnd.length;
                 runCount = DefnCount;
                 pLabel = " After SecFnd ";
				 NextCursor = HttpResp["pagination_information"]["next_cursor"];

                 while (NextCursor != undefined) {

                      Resp = DT_Connect(inputs.restMsg, NextCursor);
                      if (Resp[0] == 0) {
					        HttpResp = JSON.parse(Resp[1]);
							NextCursor = HttpResp["pagination_information"]["next_cursor"];
							SecFnd = HttpResp["security_findings"];
                            for (d=0;d < SecFnd.length; d++) {
						          OutDefnBuf["security_findings"].push(SecFnd[d]);
                            } 
                            pLabel = " After NextCurs Val: " + NextCursor;
 				            DefnCount += SecFnd.length;

			   	            if  (DefnCount >= 200) {
						        AttCount++;
                                runCount += DefnCount;
						        fileName = "DT_Vuln_Defn_" + AttCount + ".json";
						        AttWrite(inputs.datasource, fileName, JSON.stringify(OutDefnBuf));
                                //gl.logInfo(gs.getMessage('sn_vul', "INTER DEFN COUNT " + DefnCount + " < ATT COUNT > " + AttCount));
						        DefnCount = 0;
                                OutDefnBuf = {"security_findings": []};
			                }
                        }
                    }
         }	
         if (DefnCount > 0) {
                  runCount += SecFnd.length;
                  gl.logInfo(gs.getMessage('sn_vul', "LAST RunCount " + runCount + " *** Defn Count *** " + DefnCount));
		          AttCount++;
		          fileName = "DT_Vuln_Defn_" + AttCount + ".json";
		          AttWrite(inputs.datasource, fileName, JSON.stringify(OutDefnBuf));
         }
	} catch(ex) {
             load_status = false;
             gl.logError(gs.getMessage('sn_vul', "Extract Data: " + " <Label> " + pLabel + " >> " + ex));
    }  

     function DT_Connect(RestMsg, nextCursor) {
		try {
            var gl = new global.GSLog("sn_vul.usb.logging.verbosity", "DT Findings ");

			var request = new sn_ws.RESTMessageV2(RestMsg, 'POST');
			var endPoint = request.getEndpoint() + nextCursor;
			request.setEndpoint(endPoint );
			request.setEccParameter("skip_sensor", "true");
			hdrs = request.getRequestHeader();
			var response = request.executeAsync();
			response.waitForResponse(150000);

			var httpResponseStatus = response.getStatusCode();
			respBody = response.haveError() ? response.getErrorMessage() : response.getBody();

			if (httpResponseStatus != 200) {
				gl.logError(gs.getMessage('sn_vul', " *** Definitions: Failed to Connect ****  " + httpResponseStatus + "  <Body> " + respBody));
				ErrCode = 1;
			} else ErrCode = 0;

			return [ErrCode, respBody];

		} catch (ex) {
		     e_msg = ex.message;
		     gl.logError(gs.getMessage('sn_vul', " *** Definitions Download Error ****  " + ex));
			 return [1, e_msg];
		}
	 }    
     function AttWrite(pDS, attName, respBody) {
	    var gl = new global.GSLog("sn_vul.usb.logging.verbosity", "DT Vuln Defn Attachment ");
		try {
			var AttID, Att = new GlideRecord('sys_data_source');

			Att.addQuery("sys_id", pDS.sys_id);
			Att.query();
			while (Att.next()) {
				var sa = new GlideSysAttachment();
				AttID  = sa.write(Att, attName, "application/json", respBody);
			}
			if (!AttID) {
                gl.logInfo(gs.getMessage('sn_vul', " *** Definitions Att Error ****  " + attName));
				return null;
			}
			else {
                //gl.logInfo(gs.getMessage('sn_vul', " ===> Definitions Att Success ****  " + attName + " <AttID> " + AttID ));
				return AttID;
			}
		} catch (e) {
			gl.logError(gs.getMessage('sn_vul', " Vuln Defn Attach : " + e));  
		}
	  }    
 }
)(inputs, outputs);

