sys_variable_value.value:
(function execute(inputs, outputs) {

  var deleteLog = "DataTheorem Remove Attachment Files from Data Source: " + inputs.dataSource1 + " <Delete Count -> ";
  var deleteCount = 0;
  var rmvStat = true;

  var attachment = new GlideSysAttachment();
  var agr = attachment.getAttachments('sys_data_source', inputs.dataSource1.sys_id);

  while(agr.next()){
    //gs.info('agr: ' + agr.getValue('sys_id'));
    attachment.deleteAttachment(agr.getValue('sys_id'));
    deleteCount = deleteCount+1;
  }

  gs.info(deleteLog + deleteCount);

  var deleteLog = "DataTheorem Remove Attachment Files from Data Source: " + inputs.dataSource2 + " <Delete Count -> ";
  var deleteCount = 0;
  var attachment = new GlideSysAttachment();
  var agr = attachment.getAttachments('sys_data_source', inputs.dataSource2.sys_id);

  while(agr.next()){
    //gs.info('agr: ' + agr.getValue('sys_id'));
    attachment.deleteAttachment(agr.getValue('sys_id'));
    deleteCount = deleteCount+1;
  }
  gs.info(deleteLog + deleteCount);

})(inputs, outputs);

